        // Application state
        let cart = [];
        let currentUser = null;
        let currentFilter = 'all';

        // Initialize the app
        document.addEventListener('DOMContentLoaded', function() {
            displayProducts(products);
            updateCartCount();
            
            // Add featured products to home page
            const featuredProducts = products.slice(0, 4);
            displayProducts(featuredProducts);
        });

        // Product display functions
        function displayProducts(productsToShow) {
            const grid = document.getElementById('productsGrid');
            grid.innerHTML = '';

            productsToShow.forEach(product => {
                const discountPercent = product.originalPrice ? 
                    Math.round(((product.originalPrice - product.price) / product.originalPrice) * 100) : 0;
                
                const productCard = document.createElement('div');
                productCard.className = 'product-card fade-in';
                productCard.innerHTML = `
                    ${product.originalPrice && discountPercent > 0 ? 
                        `<div class="product-badge">-${discountPercent}%</div>` : ''}
                    <div class="product-image">
                        <img src="${product.image}" alt="${product.name}">
                    </div>
                    <div class="product-info">
                        <h3 class="product-title">${product.name}</h3>
                        <div class="product-rating">
                            <div class="rating-stars">
                                ${'★'.repeat(Math.floor(product.rating))}${'☆'.repeat(5 - Math.floor(product.rating))}
                            </div>
                            <span class="rating-count">(${product.reviews})</span>
                        </div>
                        <div class="product-price">
                            $${product.price.toFixed(2)}
                            ${product.originalPrice ? 
                                `<span class="original-price">$${product.originalPrice.toFixed(2)}</span>` : ''}
                            ${discountPercent > 0 ? 
                                `<span class="discount-percent">-${discountPercent}%</span>` : ''}
                        </div>
                        <p class="product-description">${product.description}</p>
                        <div class="product-actions">
                            <button class="btn-add-cart" onclick="addToCart(${product.id})">
                                <i class="fas fa-shopping-cart"></i> Add to Cart
                            </button>
                            <button class="btn-wishlist" onclick="addToWishlist(${product.id})">
                                <i class="far fa-heart"></i>
                            </button>
                        </div>
                    </div>
                `;
                grid.appendChild(productCard);
            });
        }

        // Search functionality
        function searchProducts() {
            const searchTerm = event.target.value.toLowerCase();
            const filteredProducts = products.filter(product => 
                product.name.toLowerCase().includes(searchTerm) ||
                product.description.toLowerCase().includes(searchTerm) ||
                product.brand.toLowerCase().includes(searchTerm)
            );
            
            if (currentFilter !== 'all') {
                const categoryFiltered = filteredProducts.filter(product => product.category === currentFilter);
                displayProducts(categoryFiltered);
            } else {
                displayProducts(filteredProducts);
            }
        }

        // Filter functionality
        function filterProducts(category) {
            currentFilter = category;
            
            // Update active filter button
            document.querySelectorAll('.filter-btn').forEach(btn => btn.classList.remove('active'));
            if (event) {
                event.target.classList.add('active');
            } else {
                // If called without event (e.g., from category cards)
                document.querySelector(`.filter-btn[onclick="filterProducts('${category}')"]`).classList.add('active');
            }

            if (category === 'all') {
                displayProducts(products);
            } else {
                const filtered = products.filter(product => product.category === category);
                displayProducts(filtered);
            }
        }

        // Sort functionality
        function sortProducts(sortBy) {
            let sortedProducts = [...products];
            
            switch(sortBy) {
                case 'price-low':
                    sortedProducts.sort((a, b) => a.price - b.price);
                    break;
                case 'price-high':
                    sortedProducts.sort((a, b) => b.price - a.price);
                    break;
                case 'name':
                    sortedProducts.sort((a, b) => a.name.localeCompare(b.name));
                    break;
                case 'rating':
                    sortedProducts.sort((a, b) => b.rating - a.rating);
                    break;
                default:
                    sortedProducts = products;
            }
            
            if (currentFilter !== 'all') {
                sortedProducts = sortedProducts.filter(product => product.category === currentFilter);
            }
            
            displayProducts(sortedProducts);
        }

        // Cart functionality
        function addToCart(productId) {
            const product = products.find(p => p.id === productId);
            const existingItem = cart.find(item => item.id === productId);

            if (existingItem) {
                existingItem.quantity += 1;
            } else {
                cart.push({...product, quantity: 1});
            }

            updateCartCount();
            updateCartDisplay();
            showNotification('Product added to cart!');
        }

        function removeFromCart(productId) {
            cart = cart.filter(item => item.id !== productId);
            updateCartCount();
            updateCartDisplay();
        }

        function updateCartCount() {
            const count = cart.reduce((total, item) => total + item.quantity, 0);
            document.querySelector('.cart-count').textContent = count;
        }

        function updateCartItemQuantity(productId, change) {
            const item = cart.find(item => item.id === productId);
            if (item) {
                item.quantity += change;
                if (item.quantity <= 0) {
                    removeFromCart(productId);
                } else {
                    updateCartCount();
                    updateCartDisplay();
                }
            }
        }

        function updateCartDisplay() {
            const cartItems = document.getElementById('cartItems');
            const cartTotal = document.getElementById('cartTotal');
            
            cartItems.innerHTML = '';
            let total = 0;

            if (cart.length === 0) {
                cartItems.innerHTML = '<p style="text-align:center;color:#666;padding:2rem;">Your cart is empty</p>';
                cartTotal.textContent = '0.00';
                return;
            }

            cart.forEach(item => {
                const cartItem = document.createElement('div');
                cartItem.className = 'cart-item';
                cartItem.innerHTML = `
                    <div class="cart-item-image">
                        <img src="${item.image}" alt="${item.name}">
                    </div>
                    <div class="cart-item-info">
                        <div class="cart-item-title">${item.name}</div>
                        <div class="cart-item-price">$${item.price.toFixed(2)}</div>
                        <div class="quantity-controls">
                            <button class="quantity-btn" onclick="updateCartItemQuantity(${item.id}, -1)">−</button>
                            <span>${item.quantity}</span>
                            <button class="quantity-btn" onclick="updateCartItemQuantity(${item.id}, 1)">+</button>
                        </div>
                    </div>
                    <button onclick="removeFromCart(${item.id})" style="background:none;border:none;color:#999;cursor:pointer;font-size:1.2rem;">×</button>
                `;
                cartItems.appendChild(cartItem);
                total += item.price * item.quantity;
            });

            cartTotal.textContent = total.toFixed(2);
        }

        function toggleCart() {
            const cartSidebar = document.getElementById('cartSidebar');
            cartSidebar.classList.toggle('open');
            updateCartDisplay();
        }

        function checkout() {
            if (cart.length === 0) {
                alert('Your cart is empty!');
                return;
            }
            
            if (!currentUser) {
                alert('Please login to checkout');
                showLogin();
                return;
            }

            alert('Checkout functionality would integrate with payment gateway here!');
            // In a real implementation, this would redirect to a checkout page
            // or show a checkout form with payment options
        }

        // Wishlist functionality
        function addToWishlist(productId) {
            const wishlistBtn = event.target.closest('.btn-wishlist');
            if (wishlistBtn) {
                const icon = wishlistBtn.querySelector('i');
                if (icon.classList.contains('far')) {
                    icon.classList.remove('far');
                    icon.classList.add('fas');
                    wishlistBtn.style.color = '#ff4500';
                    showNotification('Product added to wishlist!');
                } else {
                    icon.classList.remove('fas');
                    icon.classList.add('far');
                    wishlistBtn.style.color = '';
                    showNotification('Product removed from wishlist!');
                }
            }
        }

