        function subscribeNewsletter(event) {
            event.preventDefault();
            const email = event.target.querySelector('input').value;
            showNotification(`Thank you for subscribing with ${email}!`);
            event.target.reset();
        }

        // User authentication
        function showLogin() {
            document.getElementById('loginModal').style.display = 'block';
        }

        function hideLogin() {
            document.getElementById('loginModal').style.display = 'none';
        }

        function showRegister() {
            document.getElementById('registerModal').style.display = 'block';
        }

        function hideRegister() {
            document.getElementById('registerModal').style.display = 'none';
        }

        function login(event) {
            event.preventDefault();
            const email = document.getElementById('loginEmail').value;
            const password = document.getElementById('loginPassword').value;

            // Simulate authentication
            if (email && password) {
                currentUser = {
                    name: 'John Doe',
                    email: email,
                    location: 'New York, USA',
                    profilePic: 'https://randomuser.me/api/portraits/men/32.jpg'
                };
                
                updateUserInterface();
                hideLogin();
                showNotification('Login successful!');
            } else {
                alert('Please enter valid credentials');
            }
        }

        function register(event) {
            event.preventDefault();
            const name = document.getElementById('registerName').value;
            const email = document.getElementById('registerEmail').value;
            const password = document.getElementById('registerPassword').value;
            const location = document.getElementById('registerLocation').value;

            if (name && email && password && location) {
                currentUser = {
                    name: name,
                    email: email,
                    location: location,
                    profilePic: `https://randomuser.me/api/portraits/${Math.random() > 0.5 ? 'men' : 'women'}/${Math.floor(Math.random() * 50)}.jpg`
                };
                
                updateUserInterface();
                hideRegister();
                showNotification('Account created successfully!');
            } else {
                alert('Please fill in all fields');
            }
        }

        function logout() {
            currentUser = null;
            updateUserInterface();
            showNotification('Logged out successfully!');
        }

        function updateUserInterface() {
            const userActions = document.querySelector('.user-actions');
            
            if (currentUser) {
                userActions.innerHTML = `
                    <span style="color: #fff; font-size: 0.9rem;">Welcome, ${currentUser.name.split(' ')[0]}</span>
                    <button class="btn-secondary" onclick="showProfile()">Profile</button>
                    <button class="btn-secondary" onclick="logout()">Logout</button>
                    <div class="cart-icon" onclick="toggleCart()">
                        <i class="fas fa-shopping-cart"></i>
                        <span class="cart-count">0</span>
                    </div>
                `;
                
                // Update profile information
                document.getElementById('profileName').textContent = currentUser.name;
                document.getElementById('profileEmail').textContent = currentUser.email;
                document.getElementById('profileNameInput').value = currentUser.name;
                document.getElementById('profileEmailInput').value = currentUser.email;
                document.getElementById('profileLocationInput').value = currentUser.location;
                
                // Update profile picture
                const profilePic = document.querySelector('.profile-pic');
                profilePic.innerHTML = `<img src="${currentUser.profilePic}" alt="Profile Picture">`;
            } else {
                userActions.innerHTML = `
                    <button class="btn-secondary" onclick="showLogin()">Login</button>
                    <button class="btn" onclick="showRegister()">Sign Up</button>
                    <div class="cart-icon" onclick="toggleCart()">
                        <i class="fas fa-shopping-cart"></i>
                        <span class="cart-count">0</span>
                    </div>
                `;
            }
            
            updateCartCount();
        }

        // Profile management
        function showProfile() {
            document.getElementById('userProfile').style.display = 'block';
            document.getElementById('home').style.display = 'none';
            document.getElementById('products').style.display = 'none';
            document.getElementById('categories').style.display = 'none';
            window.scrollTo({ top: 0, behavior: 'smooth' });
        }

        function hideProfile() {
            document.getElementById('userProfile').style.display = 'none';
            document.getElementById('home').style.display = 'block';
            document.getElementById('products').style.display = 'block';
            document.getElementById('categories').style.display = 'block';
        }

        function updateProfile() {
            const name = document.getElementById('profileNameInput').value;
            const email = document.getElementById('profileEmailInput').value;
            const location = document.getElementById('profileLocationInput').value;

            if (currentUser) {
                currentUser.name = name;
                currentUser.email = email;
                currentUser.location = location;
                
                updateUserInterface();
                showNotification('Profile updated successfully!');
            }
        }

        // Utility functions
        function scrollToProducts() {
            document.getElementById('products').scrollIntoView({ behavior: 'smooth' });
        }

        function scrollToCategories() {
            document.getElementById('categories').scrollIntoView({ behavior: 'smooth' });
        }

        function showNotification(message) {
            // Create notification element
            const notification = document.createElement('div');
            notification.style.cssText = `
                position: fixed;
                top: 100px;
                right: 20px;
                background: #000000;
                color: #ffffff;
                padding: 1rem 2rem;
                border-radius: 6px;
                z-index: 3000;
                font-family: 'Afacad', sans-serif;
                font-weight: 600;
                animation: slideInRight 0.3s ease-out;
                display: flex;
                align-items: center;
                gap: 0.5rem;
            `;
            notification.innerHTML = `
                <i class="fas fa-check-circle"></i>
                <span>${message}</span>
            `;
            
            document.body.appendChild(notification);
            
            // Remove notification after 3 seconds
            setTimeout(() => {
                notification.style.animation = 'slideOutRight 0.3s ease-in';
                setTimeout(() => {
                    document.body.removeChild(notification);
                }, 300);
            }, 3000);
        }

        // Mobile menu functionality
        function toggleMobileMenu() {
            const navLinks = document.querySelector('.nav-links');
            navLinks.classList.toggle('open');
        }

        // Back to top functionality
        function scrollToTop() {
            window.scrollTo({ top: 0, behavior: 'smooth' });
        }

        // Show/hide back to top button
        window.addEventListener('scroll', function() {
            const backToTop = document.querySelector('.back-to-top');
            if (window.pageYOffset > 300) {
                backToTop.classList.add('show');
            } else {
                backToTop.classList.remove('show');
            }
        });

        // Close modals when clicking outside
        window.addEventListener('click', function(event) {
            const loginModal = document.getElementById('loginModal');
            const registerModal = document.getElementById('registerModal');
            
            if (event.target === loginModal) {
                hideLogin();
            }
            if (event.target === registerModal) {
                hideRegister();
            }
        });

        // Keyboard navigation
        document.addEventListener('keydown', function(event) {
            if (event.key === 'Escape') {
                hideLogin();
                hideRegister();
                document.getElementById('cartSidebar').classList.remove('open');
            }
        });

